                        ROW.appendChild(NAME)
                        C.appendChild(ROW)
                      })
                      drop_menu.appendChild(C)
                      document.querySelector(".history-mulitple-spec").style.display = ''
                    }else{
                      document.querySelector(".history-mulitple-spec").style.display = 'none'
                    }
                  }

                  priceHistoryData = newArray

